import eof from './eof.js';
import toAsyncIterator from './toAsyncIterator.js';

/**
 * Class that wraps a callable with a tape.
 * Do not use directly. Use {@link fromCallable} instead.
 */
export default class Tape {
	/**
	 * The constructor. Stores the callable that yields values to put on the tape.
	 *
	 * @param {Callable} callable - The callable to use.
	 */
	constructor(callable) {
		/**
		 * The callable yielding values to put on the tape.
		 * @type {Callable}
		 */
		this.callable = callable;

		/**
		 * Buffer used to implement {@link Tape#unread}.
		 * @type {Array}
		 */
		this.buffer = [];

		/**
		 * The eof symbol.
		 * @type {String}
		 */
		this.eof = eof;
	}

	/**
	 * Returns the next token on the tape or {@link Tape#eof}
	 * if the tape has been exhausted.
	 *
	 * @returns {Object} The next token on the tape or {@link Tape#eof}.
	 */
	async read() {
		if (this.buffer.length > 0) return this.buffer.pop();

		const state = await this.callable();

		if (state.done) return this.eof;

		return state.value;
	}

	/**
	 * Puts a token back on the tape. If {@link Tape#read} is
	 * used just after, this token will be returned.
	 *
	 * @param {Object} token - The token to put back on the tape.
	 */
	unread(token) {
		// Should this be async too ?
		this.buffer.push(token);
	}

	/**
	 * Skips the next token on the tape.
	 */
	async skip() {
		if (this.buffer.length > 0) this.buffer.pop();
		else await this.callable();
	}

	/**
	 * Skip the next `n` tokens on the tape.
	 *
	 * @param {Number} n - The number of tokens to skip.
	 */
	async skipMany(n) {
		while (n-- > 0) await this.skip(); // eslint-disable-line no-await-in-loop
	}

	/**
	 * Returns an async iterator on the tokens of the tape.
	 *
	 * @example
	 * fromString('abc');
	 * for await ( const token of tape ) console.log(token) ;
	 *
	 * @returns {AsyncIterator} Iterator on the tokens of the tape.
	 */
	[Symbol.asyncIterator]() {
		return toAsyncIterator(this);
	}

	/**
	 * Explicitely throws when trying to access iterator symbol.
	 *
	 * @throws {Error} Always.
	 */
	[Symbol.iterator]() {
		throw new Error(
			'Not implemented. A tape has no *synchronous* iterator. Use `toArray` or `toString` instead.',
		);
	}
}

/**
 * Utility function to chain an async iterable of async iterables.
 *
 * @private
 * @param {AsyncIterable} iterables - the input iterable of iterables.
 * @returns {AsyncIterable} The chain of the input iterables.
 */
export default async function* asyncIterableChain(iterables) {
	for await (const iterable of iterables) {
		for await (const item of iterable) yield item;
	}
}

/**
 * Utility function to apply a sync callable to each item of an async iterable.
 *
 * @private
 * @param {Callable} callable - The callable to apply.
 * @param {AsyncIterable} iterable - The iterable to process.
 * @returns {AsyncIterable} The iterable of mapped values.
 */
export default async function* asyncIterableMap(callable, iterable) {
	// Should the callable also be async ?
	for await (const item of iterable) yield callable(item);
}

/**
 * Spreads an asynchronous iterable inside a new array.
 *
 * @private
 * @param {AsyncIterable} asyncIterable - the input iterable.
 * @returns {Array} A new array filled with the elements of the input iterable.
 */
export default async function asyncIterableToArray(asyncIterable) {
	const array = [];
	for await (const item of asyncIterable) array.push(item);
	return array;
}

import fromAsyncIterable from './fromAsyncIterable.js';
import toAsyncIterable from './toAsyncIterable.js';
import asyncIterableChain from './asyncIterableChain.js';
import asyncIterableMap from './asyncIterableMap.js';

/**
 * Converts a tape of tapes of tokens to a tape of tokens.
 * @param {Tape} tape - The tape of tapes of tokens to read from.
 * @return {Tape} The converted tape of tokens.
 */
export default function chain(tape) {
	return fromAsyncIterable(
		asyncIterableChain(
			asyncIterableMap(toAsyncIterable, toAsyncIterable(tape)),
		),
	);
}

/**
 * Shared object used as end of file.
 */
export default {};

/**
 * Exhausts a tape.
 * @param {Tape} tape - The tape to read from.
 */
export default async function exhaust(tape) {
	while ((await tape.read()) !== tape.eof); // eslint-disable-line no-await-in-loop
}

import fromIterable from './fromIterable.js';

/**
 * Converts an array to a tape.
 *
 * @function
 * @param {Array} _array - the array to convert
 * @returns {Tape}
 */
export default fromIterable;

import fromIterator from './fromIterator.js';

/**
 * Converts an asynchronous iterable to a tape.
 *
 * @function
 * @param {AsyncIterable} _iterable - The iterable to convert.
 * @returns {Tape} The converted tape.
 */
export default (_iterable) => fromIterator(_iterable[Symbol.asyncIterator]());

import Tape from './Tape.js';

/**
 * Converts a callable to a tape.
 *
 * @function
 * @param {Callable} callable - The callable to convert.
 * @returns {Tape} The converted tape.
 */
export default (callable) => new Tape(callable);

import fromIterator from './fromIterator.js';

/**
 * Converts an iterable to a tape.
 *
 * @function
 * @param {Iterable} iterable - The iterable to convert.
 * @returns {Tape} The converted tape.
 */
export default (iterable) => fromIterator(iterable[Symbol.iterator]());

import fromCallable from './fromCallable.js';

/**
 * Converts an iterator to a tape.
 *
 * @function
 * @param {Iterator} iterator - The iterator to convert.
 * @returns {Tape} The converted tape.
 */
export default (iterator) => fromCallable(iterator.next.bind(iterator));

import fromAsyncIterable from './fromAsyncIterable.js';

/**
 * Converts a ReadStream object to an async iterable of tokens.
 *
 * @private
 * @param {ReadStream} readStream - The ReadStream object to convert.
 * @returns {AsyncIterable} The converted iterable.
 */
export async function* _fromReadStream(readStream) {
	for await (const chunk of readStream) {
		for (const piece of chunk) yield piece;
	}
}

/**
 * Converts a ReadStream object to a tape.
 *
 * @function
 * @param {ReadStream} readStream - The ReadStream object to convert.
 * @returns {Tape} The converted tape.
 */
export default (readStream) => fromAsyncIterable(_fromReadStream(readStream));

import fromIterable from './fromIterable.js';

/**
 * Converts a string to a tape.
 *
 * @example
 * fromString( '1,3,2' ) ;
 *
 * @function
 * @param {String} _string - the string to convert
 * @returns {Tape}
 */
export default fromIterable;

import split from './split.js';
import chain from './chain.js';

/**
 * Filters a tape by ignoring a given set of tokens.
 * @param {Tape} tape - The tape to filter.
 * @param {Iterable} toignore - The tokens to ignore.
 * @returns {Tape} The input tape but with some tokens ignored.
 */
export default function ignore(tape, toignore) {
	return chain(split(tape, toignore));
}

export {default as Tape} from './Tape.js';
export {default as asyncIterableChain} from './asyncIterableChain.js';
export {default as asyncIterableMap} from './asyncIterableMap.js';
export {default as asyncIterableToArray} from './asyncIterableToArray.js';
export {default as chain} from './chain.js';
export {default as eof} from './eof.js';
export {default as exhaust} from './exhaust.js';
export {default as fromArray} from './fromArray.js';
export {default as fromAsyncIterable} from './fromAsyncIterable.js';
export {default as fromCallable} from './fromCallable.js';
export {default as fromIterable} from './fromIterable.js';
export {default as fromIterator} from './fromIterator.js';
export {default as fromReadStream} from './fromReadStream.js';
export {default as fromString} from './fromString.js';
export {default as ignore} from './ignore.js';
export {default as map} from './map.js';
export {default as skip} from './skip.js';
export {default as split} from './split.js';
export {default as toArray} from './toArray.js';
export {default as toAsyncCallable} from './toAsyncCallable.js';
export {default as toAsyncIterable} from './toAsyncIterable.js';
export {default as toAsyncIterator} from './toAsyncIterator.js';
export {default as toString} from './toString.js';

import fromAsyncIterable from './fromAsyncIterable.js';
import toAsyncIterable from './toAsyncIterable.js';
import asyncIterableMap from './asyncIterableMap.js';

/**
 * Applies a callable to each token of a tape.
 * @param {Callable} callable - the callable to apply
 * @param {Tape} tape - the tape to process
 * @returns {Tape} The processed tape.
 */
export default function map(callable, tape) {
	return fromAsyncIterable(asyncIterableMap(callable, toAsyncIterable(tape)));
}

/**
 * Outputs the next token of a tape that is not contained in a given toskip
 * set.
 *
 * @private
 * @param {Tape} tape - The input tape.
 * @param {Iterable} toskip - The (hashable) tokens to skip.
 * @returns {Object} The next token not in `toskip`.
 */
export default async function skip(tape, toskip) {
	const _toskip = new Set(toskip);

	for (;;) {
		const token = await tape.read(); // eslint-disable-line no-await-in-loop
		if (!_toskip.has(token)) return token;
	}
}

import fromAsyncIterable from './fromAsyncIterable.js';
import exhaust from './exhaust.js';

/**
 * Splits the input tape into a tape of tapes according to some set of
 * separators. See {@link _split} for the actual implementation.
 *
 * @param {Tape} tape - The input tape.
 * @param {Iterable} sep - An iterable of separators.
 * @returns {Tape} A tape of tapes.
 */
export default function split(tape, sep) {
	return fromAsyncIterable(_split(tape, sep));
}

/**
 * Same as {@link split}, but returns an iterable rather than a tape.
 *
 * @private
 * @param {Tape} tape - The input tape.
 * @param {Iterable} sep - An iterable of separators.
 * @returns {AsyncIterable} An iterable of tapes.
 */
export async function* _split(tape, sep) {
	const _sep = new Set(sep);

	while (true) {
		const token = await tape.read(); // eslint-disable-line no-await-in-loop

		if (token === tape.eof) break;

		if (_sep.has(token)) continue;

		const group = fromAsyncIterable(
			(async function* () {
				yield token;

				while (true) {
					const token = await tape.read(); // eslint-disable-line no-await-in-loop

					if (_sep.has(token)) break;

					yield token;
				}
			})(),
		);

		yield group;

		await exhaust(group); // eslint-disable-line no-await-in-loop
	}
}

import toAsyncIterable from './toAsyncIterable.js';
import asyncIterableToArray from './asyncIterableToArray.js';

/**
 * Converts a tape to an array.
 *
 * @function
 * @param {Tape} tape - The tape to read from.
 * @returns {Array} The converted array.
 */
export default (tape) => asyncIterableToArray(toAsyncIterable(tape));

import toAsyncIterator from './toAsyncIterator.js';

/**
 * Converts a tape to a callable.
 *
 * @function
 * @param {Tape} tape - The tape to read from.
 * @return {Callable} The converted callable.
 */
export default (tape) => {
	const it = toAsyncIterator(tape);
	return it.next.bind(it);
};

/**
 * Converts a tape to an asynchronous iterable.
 *
 * @param {Tape} tape - The tape to read from.
 * @returns {AsyncIterable} The converted iterable.
 */
export default async function* toAsyncIterable(tape) {
	while (true) {
		const token = await tape.read(); // eslint-disable-line no-await-in-loop

		if (token === tape.eof) break;

		yield token;
	}
}

import toAsyncIterable from './toAsyncIterable.js';

/**
 * Converts a tape to an async iterator.
 *
 * @function
 * @param {Tape} tape - The tape to read from.
 * @returns {AsyncIterator} The converted iterator.
 */
export default (tape) => toAsyncIterable(tape)[Symbol.asyncIterator]();

import toArray from './toArray.js';

/**
 * Converts a tape to a string.
 *
 * @function
 * @param {Tape} tape - The tape to read from.
 * @returns {String} The converted string.
 */
export default async (tape) => (await toArray(tape)).join('');

