import fromAsyncIterable from './fromAsyncIterable' ;
import toAsyncIterable from './toAsyncIterable' ;
import asyncIterableMap from './asyncIterableMap' ;

/**
 * Applies a callable to each token of a tape.
 * @param {Callable} callable - the callable to apply
 * @param {Tape} tape - the tape to process
 */
export default function map ( callable , tape ) {

	return fromAsyncIterable( asyncIterableMap( callable , toAsyncIterable( tape ) ) ) ;

}

/**
 * Outputs the next token of a tape that is not contained in a given toskip
 * set.
 *
 * @private
 * @param {Tape} tape - The input tape.
 * @param {Iterable} toskip - The (hashable) tokens to skip.
 * @returns {Object} The next token not in `toskip`.
 */
export default async function skip ( tape , toskip ) {

	const _toskip = new Set( toskip ) ;

	while ( true ) {

		const token = await tape.read( ) ;

		if ( token === tape.eof ) return tape.eof ;

		if ( _toskip.has( token ) ) continue ;

		return token ;

	}

}

import fromAsyncIterable from './fromAsyncIterable' ;
import exhaust from './exhaust' ;

/**
 * Splits the input tape into a tape of tapes according to some set of
 * separators. See {@link _split} for the actual implementation.
 *
 * @param {Tape} tape - The input tape.
 * @param {Iterable} sep - An iterable of separators.
 * @returns {Tape}
 */
export default function split ( tape , sep ) {

	return fromAsyncIterable( _split( tape , sep ) ) ;

}

/**
 * Same as {@link split}, but returns an iterator rather than a tape.
 *
 * @private
 * @param {Tape} tape - The input tape.
 * @param {Iterable} sep - An iterable of separators.
 * @returns {Iterator}
 */
export async function* _split ( tape , sep ) {

	const _sep = new Set( sep ) ;

	while ( true ) {

		const token = await tape.read( ) ;

		if ( token === tape.eof ) break ;

		if ( _sep.has( token ) ) continue ;

		const group = fromAsyncIterable( ( async function* ( ) {

			yield token ;

			while ( true ) {

				const token = await tape.read( ) ;

				if ( _sep.has( token ) ) break ;

				yield token ;

			}

		} )( ) ) ;

		yield group ;

		await exhaust( group ) ;

	}

}

import toAsyncIterable from './toAsyncIterable' ;
import asyncIterableToArray from './asyncIterableToArray' ;

/**
 * Converts a tape to an array.
 *
 * @function
 * @param {Tape} tape - the tape to read from
 * @returns {Array}
 */
export default async tape => asyncIterableToArray( toAsyncIterable( tape ) ) ;

import toAsyncIterator from './toAsyncIterator' ;

/**
 * Converts a tape to a callable.
 *
 * @function
 * @param {Tape} tape - the tape to read from
 * @return {Callable}
 */
export default tape => {
	const it = toAsyncIterator( tape ) ;
	return it.next.bind( it ) ;
} ;

/**
 * Converts a tape to an asynchronous iterable.
 *
 * @param {Tape} tape - the tape to read from
 * @returns {AsyncIterable}
 */
export default async function* toAsyncIterable ( tape ) {

	while ( true ) {

		const token = await tape.read( ) ;

		if ( token === tape.eof ) break ;

		yield token ;

	}

}

import toAsyncIterable from './toAsyncIterable' ;

/**
 * Converts a tape to an async iterator.
 *
 * @function
 * @param {Tape} tape - the tape to read from
 * @returns {AsyncIterator}
 */
export default tape => toAsyncIterable( tape )[Symbol.asyncIterator]( ) ;

import toArray from './toArray' ;

/**
 * Converts a tape to a string.
 *
 * @function
 * @param {Tape} tape - the tape to read from
 * @returns {String}
 */
export default async tape => (await toArray( tape )).join( '' ) ;

[@aureooms/js-tape](https://aureooms.github.io/js-tape)
==

Tape library for JavaScript. Served with asynchronous goodness.
See [docs](https://aureooms.github.io/js-tape/index.html).

```js
import tape from '@aureooms/js-tape' ;
const myTape = tape.fromString( 'abracadabra' ) ;
// // OR
// const myTape = tape.fromArray( ... ) ;
// const myTape = tape.fromCallable( ... ) ;
// const myTape = tape.fromIterable( ... ) ;
// const myTape = tape.fromAsyncIterable( ... ) ;
// const myTape = tape.fromIterator( ... ) ;
// const myTape = tape.fromReadStream( ... ) ;

myTape.read()
      .then( character => console.log(character) ) // 'a'
      .then( () => myTape.read() )
      .then( character => console.log(character) ) // 'b'
      .then( () => myTape.unread('Z') )
      .then( () => myTape.unread('X') )
      .then( () => myTape.read() )
      .then( character => console.log(character) ) // 'X'
      .then( () => myTape.read() )
      .then( character => console.log(character) ) // 'Z'
      .then( () => myTape.read() )
      .then( character => console.log(character) ) // 'r'
      // ...
```

